selector: "app-parent",
templateUrl: "./parent.component.html",
styleUrls: ["./parent.component.css"]
})
export class ParentComponent implements OnInit {
tab: Array<string> = ["premier", "deuxième", "troisième"];
nord = "Lille"; sud = "Marseille"; capitale = "Paris";
constructor() { }
ngOnInit() { }
}
